    <div className="card p-4 shadow-sm bg-light-blue">
      <h1 className="text-center text-white mb-4">Record Keeper</h1>
      <div className="row">
        <div className="col-md-6">
          <h2 className="text-white">Insert a Record</h2>
          <div className="mb-3">
            <input 
              type="text" 
              value={record} 
              onChange={(e) => setRecord(e.target.value)} 
              className="form-control"
              placeholder="Enter a record" 
            />
          </div>
          <button className="btn btn-primary w-100 mb-2" onClick={handleAddRecord}>Add Record</button>
          {message && <div className="alert alert-info mt-2">{message}</div>}
        </div>
        <div className="col-md-6">
          <h2 className="text-white">Get the Last Record</h2>
