import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [record, setRecord] = useState('');
  const [lastRecord, setLastRecord] = useState(null);

  const handleAddRecord = () => {
    if (record.trim() !== '') {
      axios.post('/record', { record })
        .then(response => {
          console.log(response.data);
          setRecord('');
        })
        .catch(error => {
          console.error('There was an error adding the record!', error);
        });
    }
  };

  const handleGetRecord = () => {
    axios.get('/record')
      .then(response => {
        setLastRecord(response.data.record);
      })
      .catch(error => {
        console.error('There was an error fetching the record!', error);
        setLastRecord(null);
      });
  };

  return (
    <div className="App">
      <h1>Record Manager</h1>
      <div className="record-input">
        <h2>Insert a Record</h2>
        <input 
          type="text" 
          value={record} 
          onChange={(e) => setRecord(e.target.value)} 
          placeholder="Enter a record" 
        />
        <button onClick={handleAddRecord}>Add Record</button>
      </div>
      <div className="record-display">
        <h2>Get the Last Record</h2>
        <button onClick={handleGetRecord}>Get Last Record</button>
        {lastRecord !== null && (
          <div className="record-card">
            <p><strong>Last Record:</strong> {lastRecord}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;

